package com.vueadminproject.backend.controller;import com.vueadminproject.backend.service.TranslateService;import com.vueadminproject.backend.utils.Result;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.security.NoSuchAlgorithmException;@RestControllerpublic class TranslateController {    @Autowired    private TranslateService translateService;    @PostMapping("/translate")    public Result<String> translate(@RequestParam("q") String q, @RequestParam("from") String from, @RequestParam("to") String to) throws NoSuchAlgorithmException {        return translateService.translate(q,from,to);    }}